import { CLEAR_ID, SEARCH_ID, SEARCH_RECIPES } from "../actions";

const initialState = {
  recipes: [],
  details: [],
};

function searchRecipes(state, payload) {
  const temp = {
    recipes: payload,
    details: state.details,
  };

  return temp;
}

function searchId(state, payload) {
  const temp = {
    recipes: [...state.recipes],
    details: payload,
  };

  return temp;
}

function clearId(state) {
  const temp = {
    recipe: [...state.recipes],
    details: [],
  };

  return temp;
}
const rootReducer = (state = initialState, action) => {
  debugger;
  switch (action.type) {
    case SEARCH_RECIPES:
      return searchRecipes(state, action.payload);

    case SEARCH_ID:
      return searchId(state, action.payload);

    case CLEAR_ID:
      return clearId(state);

    default:
      return state;
  }
};

export default rootReducer;
