import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { getId } from "../../redux/actions";
import "./Style.css";

export default function DetailsCard() {
  const [details, setDetails] = useState([]);
  const dispatch = useDispatch();

  useEffect(() => {
    const id = "1";
    const fetchPost = async () => {
      const res = await axios.get(
        `http://localhost:3001/recipes/${id}?summary=summary&steps=steps&healthScore=healthScore&image=image`
      );
      setDetails(res.data);
    };
    fetchPost();
  }, [dispatch]);
  console.log(details);

  return (
    <div className="boxDetails">
      <span className="name">
        <strong>Name:</strong>
      </span>
      <div className="cardDetails">
        <img className="detailsImage" src={details.image} alt="recipe" />
        <div>
          <div>
            <strong>Summary:</strong>
          </div>
        </div>
        <span>
          <strong>Step by step</strong>
        </span>
        <span>Steps:</span>
        <span>Ingredients:</span>
        <span>Equipment:</span>
        <div>
          <h3>Diets</h3>
        </div>
        <span className="columns">
          {/* {.map((d) => ( */}
          <li className="diets"></li>
          {/* ))} */}
        </span>
      </div>
    </div>
  );
}
