import { useState } from "react";
import axios from "axios";
import "./Style.css";

function CreateRecipe() {
  const [name, setName] = useState("");
  const [summary, setSummary] = useState("");
  const [healthScore, setHealthScore] = useState(0);
  const [step, setStep] = useState([]);
  const [showstep, setShowStep] = useState("");
  const [ingredients, setIngredients] = useState([]);
  const [showIngedient, setShowIngredient] = useState("");
  const [equipment, setEquipment] = useState([]);
  const [showEquipment, setShowEquipment] = useState("");
  const [image, setImage] = useState("");
  const [diets, setDiets] = useState([]);

  function handleChangeName(e) {
    setName(e.target.value);
  }

  function handleChangeSummary(e) {
    setSummary(e.target.value);
  }

  function handleChangeHealthScore(e) {
    setHealthScore(e.target.value);
  }

  function handleChangeStep(e) {
    setShowStep(e.target.value);
  }

  function handleClickSteps(e) {
    e.preventDefault();
    setStep([...step, { name: showstep }]);
  }

  function handleChangeIngredients(e) {
    setShowIngredient(e.target.value);
  }

  function handleClickIngredients(e) {
    e.preventDefault();
    setIngredients([...ingredients, { name: showIngedient }]);
  }

  function handleChangeEquipment(e) {
    setShowEquipment(e.target.value);
  }

  function handleClickEquipment(e) {
    e.preventDefault();
    setEquipment([...equipment, { name: showEquipment }]);
  }

  function handleChangeImage(e) {
    setImage(e.target.value);
  }

  function handleCheckbox(e) {
    if (diets.indexOf(e.target.value) === -1) {
      setDiets([...diets, e.target.value]);
    } else {
      setDiets(diets.filter((item) => item !== e.target.value));
    }
  }

  function handleClick(e) {
    e.preventDefault();
    if (name === "" || summary === "") return;

    if (image === "")
      setImage(
        "https://www.food4fuel.com/wp-content/uploads/woocommerce-placeholder-600x600.png"
      );

    // axios
    //   .post("http://localhost:3001/recipes", {
    //     name: name,
    //     summary: summary,
    //     healthScore: healthScore,
    //     analyzedInstructions: [
    //       {
    //         steps: [
    //           { step: step, ingredients: ingredients, equipment: equipment },
    //         ],
    //       },
    //     ],
    //     image: image,
    //     diets: diets,
    //   })
    //   .then((response) => console.log(response))
    //   .catch((err) => console.log(err));

    console.log({
      name: name,
      summary: summary,
      healthScore: healthScore,
      analyzedInstructions: [
        {
          steps: [
            { step: step, ingredients: ingredients, equipment: equipment },
          ],
        },
      ],
      image: image,
      diets: diets,
    });
  }

  return (
    <div className="boxCreate">
      <div className="cardCreate">
        <>
          <h3 className="title">Name</h3>
          <input className="bar" type="text" onChange={handleChangeName} />
        </>
        <>
          <h3 className="title">Summary</h3>
          <input type="text" onChange={handleChangeSummary} />
        </>
        <>
          <h3 className="title">Health Score</h3>
          <input type="number" onChange={handleChangeHealthScore} />
        </>
        <h3 className="title">Steps</h3>
        <>
          {step.map((e) => {
            return (
              <div>
                <>{e.name}</>
              </div>
            );
          })}
          <input type="text" onChange={handleChangeStep} />
          <button onClick={handleClickSteps}>Add Step</button>
        </>
        <>
          <h3 className="title">Ingredients</h3>
          {ingredients.map((e) => {
            return (
              <div>
                <>{e.name}</>
              </div>
            );
          })}
          <input type="text" onChange={handleChangeIngredients} />
          <button onClick={handleClickIngredients}>Add Ingredient</button>
        </>
        <>
          <h3 className="title">Equipment Needed</h3>
          {equipment.map((e) => {
            return (
              <div>
                <>{e.name}</>
              </div>
            );
          })}
          <input type="text" onChange={handleChangeEquipment} />
          <button onClick={handleClickEquipment}>Add Equipment</button>
        </>
        <>
          <h3 className="title">Image</h3>
          <input type="text" onChange={handleChangeImage} />
        </>
        <>
          <h3 className="title">Diets</h3>
          <div>
            <>Gluten Free</>
            <input
              type="checkbox"
              name="diets"
              value="Gluten Free"
              onChange={handleCheckbox}
            />
            <>Dairy Free</>
            <input
              type="checkbox"
              name="diets"
              value="Dairy Free"
              onChange={handleCheckbox}
            />
            <>Ketogenic</>
            <input
              type="checkbox"
              name="diets"
              value="Ketogenic"
              onChange={handleCheckbox}
            />
            <>Vegetarian</>
            <input
              type="checkbox"
              name="diets"
              value="Vegetarian"
              onChange={handleCheckbox}
            />
            <>Lacto-Vegetarian</>
            <input
              type="checkbox"
              name="diets"
              value="Lacto-Vegetarian"
              onChange={handleCheckbox}
            />
            <>Ovo-Vegetarian</>
            <input
              type="checkbox"
              name="diets"
              value="Ovo-Vegetarian"
              onChange={handleCheckbox}
            />
            <>Vegan</>
            <input
              type="checkbox"
              name="diets"
              value="Vegan"
              onChange={handleCheckbox}
            />
            <>Pescetarian</>
            <input
              type="checkbox"
              name="diets"
              value="Pescetarian"
              onChange={handleCheckbox}
            />
            <>Paleo</>
            <input
              type="checkbox"
              name="diets"
              value="Paleo"
              onChange={handleCheckbox}
            />
            <>Primal</>
            <input
              type="checkbox"
              name="diets"
              value="Primal"
              onChange={handleCheckbox}
            />
            <>low FODMAP</>
            <input
              type="checkbox"
              name="diets"
              value="low FODMAP"
              onChange={handleCheckbox}
            />
            <>Whole30</>
            <input
              type="checkbox"
              name="diets"
              value="Whole30"
              onChange={handleCheckbox}
            />
          </div>
        </>
        <button onClick={handleClick}>Create New Recipe</button>
      </div>
    </div>
  );
}

export default CreateRecipe;
