import { useDispatch } from "react-redux";

const axios = require("axios");
export const GET_RECIPES = "GET_RECIPIES";
export const ALL_RECIPES = "ALL_RECIPES";

export const getRecipes = (name) => (dipatch) => {
  return axios
    .get(`http://localhost:3001/recipes?name=${name}`)
    .then((response) => response.data)
    .then((data) => useDispatch({ type: GET_RECIPES, payload: data }));
};

export const allRecipes = (prop, order) => (dipatch) => {
  return axios
    .get(`http://localhost:3001/recipes?prop=${prop}&order=${order}`)
    .then((response) => response.data)
    .then((data) => useDispatch({ type: ALL_RECIPES, payload: data }));
};

export const getDetails = () => (dipatch) => {};
