import { useState } from "react";
import axios from "axios";
import "./Style.css";

function CreateRecipe() {
  const [name, setName] = useState("");
  const [summary, setSummary] = useState("");
  const [healthScore, setHealthScore] = useState("");
  const [step, setStep] = useState("");
  const [ingedients, setIngredients] = useState([]);
  const [equipment, setEquipment] = useState([]);
  const [image, setImage] = useState("");
  const [diets, setDiets] = useState([]);

  function handleChangeName(e) {}

  function handleChangeSummary(e) {}

  function handleChangeHealthScore(e) {}

  function handleChangeStep(e) {}

  function handleChangeIngredients(e) {}

  function handleChangeEquipment(e) {}

  function handleChangeImage(e) {}

  function handleChangeDiets(e) {}

  function handleSubmit(e) {
    e.preventDefault();
    axios.post("http://localhost:3001/recipes", {
      name: name,
      summary: summary,
      healthScore: healthScore,
      analyzedInstructions: [
        {
          steps: [
            { step: step, ingredients: ingedients, equipment: equipment },
          ],
        },
      ],
      image: image,
      diets: diets,
    });
  }

  return (
    <div className="boxCreate">
      <div className="cardCreate">
        <input type="text" onChange={handleChangeName} />
        <input type="text" onChange={handleChangeSummary} />
        <input type="text" onChange={handleChangeHealthScore} />
        <input type="text" onChange={handleChangeStep} />
        <input type="text" onChange={handleChangeIngredients} />
        <input type="text" onChange={handleChangeEquipment} />
        <input type="text" onChange={handleChangeImage} />
        <input type="text" onChange={handleChangeDiets} />
        <button onSubmit={handleSubmit}>Create New Recipe</button>
      </div>
    </div>
  );
}

export default CreateRecipe;
