import "./Style.css";
import CardsCompiler from "../RecipeCards/CardsCompiler";
import { recipe } from "../db/db";
import React, { useState, useEffect } from "react";
import axios from "axios";
import Posts from "./Posts";
import Pagination from "./Pagination";

function Home() {
  const [recipeState, setRecipe] = useState([]);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(12);

  useEffect(() => {
    const fetchPost = async () => {
      setLoading(true);
      const res = await axios.get("http://localhost:3001/recipes/get");
      setPosts(res.data);
      setLoading(false);
    };

    fetchPost();
  }, []);
  console.log(posts);

  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="">
      <input
        type="text"
        placeholder="Search..."
        className="search"
        on
        onChange={handleChange}
      />
      <CardsCompiler recipe={currentPosts} />
      {/* <Posts posts={currentPosts} loading={loading} /> */}
      <Pagination
        postsPerPage={postsPerPage}
        totalPosts={posts.length}
        paginate={paginate}
      />
    </div>
  );
}

export default Home;
