import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { getId } from "../../redux/actions";
import "./Style.css";

export default function DetailsCard() {
  const [details, setDetails] = useState([]);
  const dispatch = useDispatch();

  useEffect(() => {
    const id = "1";
    const fetchPost = async () => {
      const res = await axios.get(
        `http://localhost:3001/recipes/${id}?summary=summary&steps=steps&healthScore=healthScore&image=image`
      );
      setDetails(res.data);
    };
    fetchPost();
  }, [dispatch]);
  console.log(details.Diets);
  return (
    <div className="boxDetails">
      <div className="cardDetails">
        <span className="detailsName">
          <strong>Name: {details.name}</strong>
        </span>
        <img className="detailsImage" src={details.image} alt="recipe" />
        <div>
          <div className="detailsSummary">
            <strong>Summary: </strong>
            {details.summary}
          </div>
        </div>
        <span>
          <strong>Step by step</strong>
        </span>
        <span>Steps:</span>
        <span>Ingredients:</span>
        <span>Equipment:</span>
        <div>
          <h3>Diets</h3>
        </div>
        {/* <span className="columns">
          {details.Diets.map((d) => (
            <li className="detailsDiets">{d}</li>
          ))}
        </span> */}
      </div>
    </div>
  );
}
