import "./Style.css";
import CardsCompiler from "../RecipeCards/CardsCompiler";
import React, { useState, useEffect } from "react";
import axios from "axios";
import Pagination from "./Pagination";
import Post from "./Post";

function Home() {
  const [recipe, setRecipe] = useState("");
  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(12);
  const [loading, setLoading] = useState(false);
  const [order, setOrder] = useState([
    { id: "1", name: "Ascending" },
    { id: "2", name: "escending" },
  ]);

  function handleChange(e) {
    // setRecipe(
    //   posts.filter((o) =>
    //     o.name.toLowerCase().includes(e.target.value.toLowerCase())
    //   )
    // );
    setRecipe(e.target.value);
  }

  useEffect(() => {
    if (recipe === "") {
      const fetchPost = async () => {
        setLoading(true);
        const res = await axios.get("http://localhost:3001/recipes/get");
        setPosts(res.data);
        setLoading(false);
      };
      fetchPost();
    } else {
      const fetchPost = async () => {
        setLoading(true);
        const res = await axios.get(
          `http://localhost:3001/recipes/?name=${recipe}`
        );
        setPosts(res.data);
        setLoading(false);
      };
      fetchPost();
    }
  }, [recipe]);

  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="">
      <input
        type="text"
        placeholder="Search..."
        className="search"
        on
        onChange={handleChange}
      />

      <Post posts={currentPosts} loading={loading} />
      <Pagination
        postsPerPage={postsPerPage}
        totalPosts={posts.length}
        paginate={paginate}
      />
    </div>
  );
}

export default Home;
