import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { getId } from "../../redux/actions";
import "./Style.css";

export default function DetailsCard() {
  const [details, setDetails] = useState({ Diets: [{ name: "" }] });
  const [steps, setSteps] = useState([]);
  const dispatch = useDispatch();

  useEffect(() => {
    const id = "1";
    const fetchPost = async () => {
      const res = await axios.get(
        `http://localhost:3001/recipes/${id}?summary=summary&steps=steps&healthScore=healthScore&image=image`
      );
      setDetails(res.data);
    };
    fetchPost();
  }, [dispatch]);

  // useEffect(() => {
  //   const middleStep
  //   const setVariables = async () => {
  //     console.log(details.steps);
  //     setSteps(await JSON.parse(details.steps));
  //   };
  //   setVariables();
  // }, [details]);

  setSteps(JSON.parse(details.steps.replace(/\\"/g, '"')));

  return (
    <div className="boxDetails">
      <div className="cardDetails">
        <span className="detailsName">
          <strong>Name: {details.name}</strong>
        </span>
        <img className="detailsImage" src={details.image} alt="recipe" />
        <div>
          <span className="title">
            <strong>Summary</strong>
          </span>
          <span className="detailsSummary">{details.summary}</span>
        </div>
        <span className="title">
          <strong>Step by step</strong>
        </span>
        <span></span>
        <span>Ingredients:</span>
        <span>Equipment:</span>
        <div className="title">
          <h3>Diets</h3>
        </div>
        <span className="detailsColumns">
          {details.Diets.map((d) => (
            <li key={d.name} className="detailsDiets">
              {d.name}
            </li>
          ))}
        </span>
      </div>
    </div>
  );
}
