import React from "react";

export const RecipeCard = (props) => {
  const diets = props.diets.map((diet) => diet.name);
  return (
    <div>
      <img src={props.image} alt="recipe" />
      <p>ID: {props.id} </p>
      <p>Name: {props.name}</p>
      {/* <p>Dietas: {diets.map((d) => d)}</p> */}
    </div>
  );
};
