import React from "react";
import "./Style.css";
import axios from "axios";
import { useHistory } from "react-router-dom";

export default function RecipeCard(props) {
  const history = useHistory();

  const routeChange = () => {
    const details = axios
      .get(
        `http://localhost:3001/recipes/${window.location.href.substring(
          window.location.href.lastIndexOf("/") + 1
        )}`
      )
      .then((response) => {
        return response.data;
      });

    let path = `/recipe/${props.recipe.id}`;
    history.push(path);
  };

  const diets = props.recipe.Diets.map((diet) => diet.name);
  return (
    <div className="cardmargin" key={props.id}>
      <button className="card" onClick={routeChange}>
        <img src={props.recipe.image} alt="recipe" class="image" />
        <div>
          {/* <p>{props.recipe.id} </p> */}
          <span className="name">
            <strong>Name:</strong> {props.recipe.name}
          </span>
        </div>
        <div>
          <h3>Diets</h3>
        </div>
        <span className="columns">
          {diets.map((d) => (
            <li class="diets">{d}</li>
          ))}
        </span>
      </button>
    </div>
  );
}
