import React from "react";

export default function RecipeCard(props) {
  const diets = props.Diets;
  const diet = diets.map((diet) => diet.name);
  return (
    <div>
      <img src={props.image} alt="recipe" />
      <div>
        <p>{props.id} </p>
        <p>Name: {props.name}</p>
        <h3>Diets</h3>
        {diet.map((d) => (
          <li key={props.id}>{d}</li>
        ))}
      </div>
    </div>
  );
}

RecipeCard({
  id: 234,
  name: "Salmon with roasted vegetables",
  Diets: [
    {
      id: 15,
      name: "Gluten Free",
      Recipe_Diet: {
        RecipeId: 234,
        DietId: 15,
      },
    },
    {
      id: 16,
      name: "Dairy Free",
      Recipe_Diet: {
        RecipeId: 234,
        DietId: 16,
      },
    },
    {
      id: 22,
      name: "Pescetarian",
      Recipe_Diet: {
        RecipeId: 234,
        DietId: 22,
      },
    },
  ],
});
