const initialState = {
  recipes: [],
  details: {},
};

function getRecipes(state, payload) {
  const temp = { ...state };
  temp.recipes = { ...payload };
  return temp;
}

function searchId(state, payload) {
  const temp = state;
  temp.details[0] = { ...payload };
  return temp;
}

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "GET_RECIPES":
      return getRecipes(state, action.payload);

    case "SEARCH_ID":
      return searchId(state, action.payload);

    default:
      return state;
  }
};

export default rootReducer;
